import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import FormLabel from '@material-ui/core/FormLabel';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import RadioGroup from '@material-ui/core/RadioGroup';
import Radio from '@material-ui/core/Radio';
import Paper from '@material-ui/core/Paper';

import { firestoreConnect } from 'react-redux-firebase';
import { useSelector, connect } from 'react-redux';
import { compose } from 'redux';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    height: 140,
    width: 100,
  },
  control: {
    padding: theme.spacing(2),
  },
}));

function  ReservatonBoxView (props)  {
  const { useState } = React;

  const reservations = useSelector(state => console.log(state) )
  const [spacing, setSpacing] = React.useState(2);
  const classes = useStyles();

  const handleChange = (event) => {
    setSpacing(Number(event.target.value));
  };

  const box = null;


  for(var a = 0 ; a < 31 ; a++){
    for(var b = b ; b < 6 ; b++){
      box[a][b] = null;
    }
  }

  if(reservations != null){
    console.log("asd")
  for(var reservation = 0 ; reservation < reservations.length ; reservations++){
    for(var room = 0 ; room < 6 ; room++){
      const reservedDays = getDaysArray(reservations[reservation].startDay,reservation[reservation].endDay);
      if(room == reservations[reservation].roomId ){
        for(var dayOfBooking of reservedDays){
          box[dayOfBooking][room] = reservation
        }
      }
      }
  }
  console.table(box);
var getDaysArray = function(start, end) {
    for(var arr=[],dt=new Date(start); dt<=end; dt.setDate(dt.getDate()+1)){
        arr.push(new Date(dt));
    }
    return arr;
};
  }
  return (
    <Grid container className={classes.root} spacing={2}>
      <Grid item xs={12}>
        <Grid container justify="center" spacing={3}>
          {[0, 1, 2].map((value) => (
            <Grid key={value} item>
              <Paper className={classes.paper} />
            </Grid>
          ))}
        </Grid>
      </Grid>
    </Grid>
  );
}

export default firestoreConnect([
  {collection: 'roomtype'}
]) (ReservatonBoxView)
